#include <stdio.h>
double a, b;
int main(void) {
    scanf("%lf %lf", &a, &b);
    if (a > 0 && b > 0) {
        printf("%.2f %.2f", a * b, 2.0 * (a + b));
    } else {
        printf("error");
    }
    printf("\n");
    return 0;
}

